let temperature = prompt("Введите температуру :");

temperature = parseFloat(temperature);


if (temperature < -20) {
    weatherFeeling = "Мороз℉";
    backgroundImage = "url('data:image/jpeg;base64,')";

} else if (temperature >= -20 && temperature < 0) {
    weatherFeeling = "Холод℉";
    backgroundImage = "url('data:image/jpeg;base64,')";

} else if (temperature >= 0 && temperature < 15) {
    weatherFeeling = "Прохладно℉";
    backgroundImage = "url('data:image/jpeg;base64,')";

} else if (temperature >= 15 && temperature < 28) {
    weatherFeeling = "Тепло℉";
    backgroundImage = "url('https://www.bing.com/images/blob?bcid=S74ppNPpmdQHqxcxoNWLuD9SqbotqVTdP2g')";

} else {
    weatherFeeling = "Жарко℉";
    backgroundImage = "url('https://admem.net/storage/meme/78/88/1391048878.jpg?1692948875')";


}
document.body.style.backgroundImage = backgroundImage;

alert(`На улице: ${weatherFeeling}`);
